import React from 'react';

import ExpenseForm from './ExpenseForm';
import './NewExpense.css';

const NewExpense = (props) => {
  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      id: 'e'+ (Math.floor(Math.random() * 100)),
      ...enteredExpenseData     
    };
    props.onAddExpense(expenseData);
  };

  let showform ;

  let [output, setOutput] = React.useState(<button onClick={showForm} >Add Expense</button>);


  showForm = () => {
    output = <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} closeForm={closeF}/> ;
  }  

  function closeF(value) {
      value == true && setOutput(<button onClick={showForm} >Add Expense</button>);
  } 


  return (
    <div className='new-expense'>
      {output}
      {/* <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} /> */}
    </div>
  );
};

export default NewExpense;
