import FormInput from './Components/FormInput';

const InvestForm = (props) => {


    function submitHandler(event) {
        props.formInputs(event.target.value);
    }

    return (
            <form className="form">
                <div className="input-group">

                <FormInput field_id="current-savings" field_label="Current Savings ($)" />
                {/* <p>
                    <label htmlFor="current-savings">Current Savings ($)</label>
                    <input type="number" id="current-savings" />
                </p> */}

                <FormInput field_id="yearly-contribution" field_label="Yearly Savings ($)" />
                {/* <p>
                    <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                    <input type="number" id="yearly-contribution" />
                </p> */}
                </div>
                <div className="input-group">

                <FormInput field_id="expected-return" field_label="Expected Interest (%, per year)" />
                {/* <p>
                    <label htmlFor="expected-return">
                    Expected Interest (%, per year)
                    </label>
                    <input type="number" id="expected-return" />
                </p> */}

                <FormInput field_id="duration" field_label="Investment Duration (years)" />
                {/* <p>
                    <label htmlFor="duration">Investment Duration (years)</label>
                    <input type="number" id="duration" />
                </p> */}
                </div>
                <p className="actions">
                <button type="reset" className="buttonAlt">
                    Reset
                </button>
                <button type="submit" className="button" onClick={(event) =>submitHandler}>
                    Calculate
                </button>
                </p>
            </form>

    );
};

export default InvestForm;
