import logo from './assets/investment-calculator-logo.png';
import DispInvest from './Components/DispInvest';
import InvestForm from './Components/InvestForm';


function App() {
  const calculateHandler = (data) => {
    // Should be triggered when form is submitted
    // You might not directly want to bind it to the submit event on the form though...

    let yearlyData = []; // per-year results

    let currentSavings = data.currS; // feel free to change the shape of this input object!
    const yearlyContribution = data.yearS; // as mentioned: feel free to change the shape...
    const expectedReturn = data.expR / 100;
    const duration = data.dur;

   // // The below code calculates yearly results (total savings, interest etc)
    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      yearlyData.push({
        // feel free to change the shape of the data pushed to the array!
        'year': i + 1,
        'interest': yearlyInterest,
        'totalS' : currentSavings,
        'cap': yearlyContribution,
      });
    }

    // do something with yearlyData ...
  };

  return (
    <div>
      <header className="header">
        <img src={logo} alt="logo" />
        <h1>Investment Calculator</h1>
      </header>

    <InvestForm formInputs={calculateHandler}/>


      {/* Todo: Show below table conditionally (only once result data is available) */}
      {/* Show fallback text if no data is available */}

      <DispInvest invest={yearlyData} />


    </div>
  );
}

export default App;
