import FormInput from './Components/FormInput';

import { useState } from 'react';

const InvestForm = (props) => {

    const [currS, setCurrS] = useState();
    const [yearS, setYearS] = useState();
    const [expR, setExpR] = useState();
    const [dur, setDur] = useState();




    function submitHandler(event) {

        event.preventDefault();

        const formData = [
            {
                'currS': currS,
                'yearS': yearS,
                'expR' : expR,
                'dur': dur,   
            }
        ];
        props.formInputs(event.target.value);
    }

    // function CurrSave() {

    // }

    return (
            <form className="form">
                <div className="input-group">

                    {/* <FormInput field_id="current-savings" field_label="Current Savings ($)" getData={() => currSave} /> */}
                    <p>
                        <label htmlFor="current-savings">Current Savings ($)</label>
                        <input type="number" id="current-savings" onChange={(event) => setCurrS(event.target.value)} />
                    </p>

                    {/* <FormInput field_id="yearly-contribution" field_label="Yearly Savings ($)" /> */}
                    <p>
                        <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                        <input type="number" id="yearly-contribution"  onChange={(event) => setYearS(event.target.value)}/>
                    </p>
                </div>
                <div className="input-group">

                    {/* <FormInput field_id="expected-return" field_label="Expected Interest (%, per year)" /> */}
                    <p>
                        <label htmlFor="expected-return">
                        Expected Interest (%, per year)
                        </label>
                        <input type="number" id="expected-return" onChange={(event) => setExpR(event.target.value)}/>
                    </p>

                    {/* <FormInput field_id="duration" field_label="Investment Duration (years)" /> */}
                    <p>
                        <label htmlFor="duration">Investment Duration (years)</label>
                        <input type="number" id="duration" onChange={(event) => setDur(event.target.value)}/>
                    </p>
                </div>
                <p className="actions">
                <button type="reset" className="buttonAlt">
                    Reset
                </button>
                <button type="submit" className="button" onClick={(event) =>submitHandler}>
                    Calculate
                </button>
                </p>
            </form>

    );
};

export default InvestForm;
